server/generate-product-images.ts.content:
import { db } from "./db";
import { products } from "@shared/schema";
import { eq, or, sql, isNull } from "drizzle-orm";
import fs from "fs";
import path from "path";
import { fileURLToPath } from 'url';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

// Function to check if image URL is broken
function isImageBroken(imgLink: string | null): boolean {
  if (!imgLink) return true;

  // Check for known broken patterns
  if (imgLink.includes('/images/I/') && !imgLink.startsWith('https://m.media-amazon.com')) {
    return true;
  }

  // Check for incomplete URLs
  if (imgLink.includes('WEBP_402378') && imgLink.endsWith('.jp')) {
    return true;
  }

  return false;
}

// Function to generate prompt from product name
function generateImagePrompt(productName: string, category: string): string {
  // Clean up the product name for better prompts
  const cleanName = productName
    .replace(/\([^)]*\)/g, '') // Remove parentheses content
    .replace(/[,]/g, '')
    .trim();

  // Extract key product type from category
  const categoryParts = category.split('|');
  const productType = categoryParts[categoryParts.length - 1] || categoryParts[0];

  return `Professional product photography of ${cleanName}, ${productType}, on clean white background, studio lighting, high quality, commercial product shot, crisp and clear, centered composition`;
}

async function generateProductImages() {
  try {
    console.log('Finding products with broken or missing images...');

    // Get products with potentially broken images
    const brokenProducts = await db
      .select()
      .from(products)
      .where(
        or(
          isNull(products.imgLink),
          eq(products.imgLink, ''),
          sql`${products.imgLink} LIKE '%/images/I/%'`,
          sql`${products.imgLink} LIKE '%.jp'`
        )
      )
      .limit(10); // Process in batches

    console.log(`Found ${brokenProducts.length} products with broken images`);

    if (brokenProducts.length === 0) {
      console.log('No products with broken images found');
      return;
    }

    // Create directory for generated images
    const generatedImagesDir = path.join(__dirname, '..', 'attached_assets', 'generated_images');
    if (!fs.existsSync(generatedImagesDir)) {
      fs.mkdirSync(generatedImagesDir, { recursive: true });
    }

    // Process each product
    for (const product of brokenProducts) {
      console.log(`Processing: ${product.productName.substring(0, 50)}...`);

      const prompt = generateImagePrompt(product.productName, product.category);
      const filename = `product_${product.id}_generated.png`;
      const imagePath = path.join('attached_assets', 'generated_images', filename);

      // Update the product with the new image path
      await db
        .update(products)
        .set({ 
          imgLink: `/${imagePath}` 
        })
        .where(eq(products.id, product.id));

      console.log(`Updated product ${product.id} with generated image path`);
    }

    console.log('Product image generation complete!');
    console.log('Note: AI image generation will be triggered when products are displayed');

  } catch (error) {
    console.error('Error generating product images:', error);
  }
}

// Run the script
generateProductImages()
  .then(() => process.exit(0))
  .catch((error) => {
    console.error(error);
    process.exit(1);
  });

server/image-generator.ts.content:
import { db } from "./db";
import { products } from "@shared/schema";
import { eq } from "drizzle-orm";
import fs from "fs";
import path from "path";
import { fileURLToPath } from 'url';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

export function generateImagePrompt(productName: string, category: string): string {
  // Clean up the product name for better prompts
  const cleanName = productName
    .replace(/\([^)]*\)/g, '') // Remove parentheses content
    .replace(/[,]/g, '')
    .replace(/[\r\n]+/g, ' ')
    .trim()
    .substring(0, 100); // Limit length

  // Extract key product type from category
  const categoryParts = category.split('|');
  const productType = categoryParts[categoryParts.length - 1] || categoryParts[0];

  return `Professional product photography of ${cleanName}, ${productType}, on clean white background, studio lighting, high quality, commercial product shot, crisp and clear, centered composition, e-commerce style`;
}

export async function ensureGeneratedImagesDir(): Promise<string> {
  const generatedImagesDir = path.join(__dirname, '..', 'attached_assets', 'generated_images');
  if (!fs.existsSync(generatedImagesDir)) {
    fs.mkdirSync(generatedImagesDir, { recursive: true });
  }
  return generatedImagesDir;
}

export async function checkAndGenerateImage(productId: string): Promise<string | null> {
  try {
    const [product] = await db
      .select()
      .from(products)
      .where(eq(products.id, productId));

    if (!product) {
      return null;
    }

    // Check if image is already generated
    if (product.imgLink && product.imgLink.includes('/generated_images/')) {
      return product.imgLink;
    }

    // Check if original image is broken
    const isBroken = !product.imgLink || 
                     product.imgLink.includes('/images/I/') ||
                     product.imgLink.endsWith('.jp');

    if (!isBroken) {
      return product.imgLink;
    }

    // Generate placeholder path for now
    const filename = `product_${productId}_placeholder.png`;
    const imagePath = `/attached_assets/generated_images/${filename}`;

    // Update database with placeholder path
    await db
      .update(products)
      .set({ imgLink: imagePath })
      .where(eq(products.id, productId));

    return imagePath;
  } catch (error) {
    console.error('Error checking/generating image:', error);
    return null;
  }
}

server/import-csv.ts.content:
import { db } from "./db";
import { products } from "@shared/schema";
import fs from "fs";
import path from "path";
import { fileURLToPath } from 'url';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

interface CSVRow {
  product_id: string;
  product_name: string;
  category: string;
  discounted_price: string;
  actual_price: string;
  discount_percentage: string;
  rating: string;
  rating_count: string;
  about_product: string;
  user_id: string;
  user_name: string;
  review_id: string;
  review_title: string;
  review_content: string;
  img_link: string;
  product_link: string;
}

function parseCSVLine(line: string): string[] {
  const result: string[] = [];
  let current = '';
  let inQuotes = false;
  let i = 0;

  while (i < line.length) {
    const char = line[i];

    if (char === '"') {
      if (inQuotes && i + 1 < line.length && line[i + 1] === '"') {
        // Double quote inside quoted field
        current += '"';
        i += 2;
      } else {
        // Toggle quote state
        inQuotes = !inQuotes;
        i++;
      }
    } else if (char === ',' && !inQuotes) {
      // Field separator
      result.push(current);
      current = '';
      i++;
    } else {
      current += char;
      i++;
    }
  }

  // Add the last field
  result.push(current);

  return result;
}

function parseCSV(csvContent: string): CSVRow[] {
  const lines = csvContent.split('\n');
  const headers = parseCSVLine(lines[0]);

  const rows: CSVRow[] = [];
  const processedProducts = new Set<string>();

  for (let i = 1; i < lines.length; i++) {
    const line = lines[i].trim();
    if (!line) continue;

    const values = parseCSVLine(line);

    if (values.length >= 16) {
      const productId = values[0];

      // Only process each product once (CSV has duplicate products with different reviews)
      if (!processedProducts.has(productId)) {
        processedProducts.add(productId);

        const row: CSVRow = {
          product_id: productId,
          product_name: values[1] || '',
          category: values[2] || '',
          discounted_price: values[3] || '',
          actual_price: values[4] || '',
          discount_percentage: values[5] || '',
          rating: values[6] || '',
          rating_count: values[7] || '',
          about_product: values[8] || '',
          user_id: values[9] || '',
          user_name: values[10] || '',
          review_id: values[11] || '',
          review_title: values[12] || '',
          review_content: values[13] || '',
          img_link: values[14] || '',
          product_link: values[15] || ''
        };

        rows.push(row);
      }
    }
  }

  return rows;
}

async function importCSV() {
  try {
    console.log('Starting CSV import...');

    const csvPath = path.join(__dirname, '..', 'attached_assets', 'amazon_1757236301638.csv');
    const csvContent = fs.readFileSync(csvPath, 'utf-8');

    console.log('Parsing CSV...');
    const rows = parseCSV(csvContent);
    console.log(`Parsed ${rows.length} unique products`);

    console.log('Inserting products into database...');
    const batchSize = 100;
    let inserted = 0;

    for (let i = 0; i < rows.length; i += batchSize) {
      const batch = rows.slice(i, i + batchSize);

      const productData = batch.map(row => ({
        id: row.product_id,
        productName: row.product_name,
        category: row.category,
        discountedPrice: row.discounted_price,
        actualPrice: row.actual_price,
        discountPercentage: row.discount_percentage,
        rating: row.rating ? parseFloat(row.rating) : null,
        ratingCount: row.rating_count,
        aboutProduct: row.about_product,
        imgLink: row.img_link,
        productLink: row.product_link,
      }));

      await db.insert(products).values(productData).onConflictDoNothing();
      inserted += batch.length;

      console.log(`Inserted ${inserted}/${rows.length} products...`);
    }

    console.log('CSV import completed successfully!');
  } catch (error) {
    console.error('Error importing CSV:', error);
    process.exit(1);
  }
}

// Run import if this script is executed directly
if (import.meta.url === `file://${process.argv[1]}`) {
  importCSV();
}

export { importCSV };

