import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { ShoppingBag } from "lucide-react";
import { useAuth } from "@/hooks/use-auth";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { signInSchema, signUpSchema } from "@shared/schema";
import { z } from "zod";
import { useToast } from "@/hooks/use-toast";

type SignInData = z.infer<typeof signInSchema>;
type SignUpData = z.infer<typeof signUpSchema>;

interface AuthModalProps {
  isOpen: boolean;
  onClose: () => void;
}

export function AuthModal({ isOpen, onClose }: AuthModalProps) {
  const [mode, setMode] = useState<"signin" | "signup">("signin");
  const { signIn, signUp, isLoading } = useAuth();
  const { toast } = useToast();

  const signInForm = useForm<SignInData>({
    resolver: zodResolver(signInSchema),
    defaultValues: {
      email: "",
      password: "",
    },
  });

  const signUpForm = useForm<SignUpData>({
    resolver: zodResolver(signUpSchema),
    defaultValues: {
      email: "",
      password: "",
      confirmPassword: "",
      name: "",
    },
  });

  const handleSignIn = async (data: SignInData) => {
    try {
      await signIn(data);
      toast({
        title: "Welcome back!",
        description: "You have been signed in successfully.",
      });
      onClose();
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to sign in",
        variant: "destructive",
      });
    }
  };

  const handleSignUp = async (data: SignUpData) => {
    try {
      await signUp(data);
      toast({
        title: "Account created!",
        description: "Welcome to ShopHub!",
      });
      onClose();
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to create account",
        variant: "destructive",
      });
    }
  };

  const resetForms = () => {
    signInForm.reset();
    signUpForm.reset();
  };

  const handleModeToggle = () => {
    setMode(mode === "signin" ? "signup" : "signin");
    resetForms();
  };

  const handleClose = () => {
    onClose();
    resetForms();
    setMode("signin");
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader className="text-center">
          <div className="flex items-center justify-center space-x-2 mb-4">
            <div className="bg-primary text-primary-foreground rounded-lg p-2">
              <ShoppingBag className="h-6 w-6" />
            </div>
            <span className="text-2xl font-bold">ShopHub</span>
          </div>
          <DialogTitle data-testid="auth-modal-title">
            {mode === "signin" ? "Sign In" : "Create Account"}
          </DialogTitle>
          <DialogDescription>
            {mode === "signin" 
              ? "Welcome back! Please sign in to your account."
              : "Join ShopHub to start shopping amazing products."
            }
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4">
          {mode === "signin" ? (
            <form onSubmit={signInForm.handleSubmit(handleSignIn)} className="space-y-4">
              <div>
                <Label htmlFor="signin-email">Email</Label>
                <Input
                  id="signin-email"
                  type="email"
                  {...signInForm.register("email")}
                  data-testid="input-signin-email"
                />
                {signInForm.formState.errors.email && (
                  <p className="text-sm text-destructive mt-1">
                    {signInForm.formState.errors.email.message}
                  </p>
                )}
              </div>
              <div>
                <Label htmlFor="signin-password">Password</Label>
                <Input
                  id="signin-password"
                  type="password"
                  {...signInForm.register("password")}
                  data-testid="input-signin-password"
                />
                {signInForm.formState.errors.password && (
                  <p className="text-sm text-destructive mt-1">
                    {signInForm.formState.errors.password.message}
                  </p>
                )}
              </div>
              <Button 
                type="submit" 
                className="w-full" 
                disabled={isLoading}
                data-testid="button-signin-submit"
              >
                {isLoading ? "Signing in..." : "Sign In"}
              </Button>
            </form>
          ) : (
            <form onSubmit={signUpForm.handleSubmit(handleSignUp)} className="space-y-4">
              <div>
                <Label htmlFor="signup-name">Full Name</Label>
                <Input
                  id="signup-name"
                  {...signUpForm.register("name")}
                  data-testid="input-signup-name"
                />
                {signUpForm.formState.errors.name && (
                  <p className="text-sm text-destructive mt-1">
                    {signUpForm.formState.errors.name.message}
                  </p>
                )}
              </div>
              <div>
                <Label htmlFor="signup-email">Email</Label>
                <Input
                  id="signup-email"
                  type="email"
                  {...signUpForm.register("email")}
                  data-testid="input-signup-email"
                />
                {signUpForm.formState.errors.email && (
                  <p className="text-sm text-destructive mt-1">
                    {signUpForm.formState.errors.email.message}
                  </p>
                )}
              </div>
              <div>
                <Label htmlFor="signup-password">Password</Label>
                <Input
                  id="signup-password"
                  type="password"
                  {...signUpForm.register("password")}
                  data-testid="input-signup-password"
                />
                {signUpForm.formState.errors.password && (
                  <p className="text-sm text-destructive mt-1">
                    {signUpForm.formState.errors.password.message}
                  </p>
                )}
              </div>
              <div>
                <Label htmlFor="signup-confirm-password">Confirm Password</Label>
                <Input
                  id="signup-confirm-password"
                  type="password"
                  {...signUpForm.register("confirmPassword")}
                  data-testid="input-signup-confirm-password"
                />
                {signUpForm.formState.errors.confirmPassword && (
                  <p className="text-sm text-destructive mt-1">
                    {signUpForm.formState.errors.confirmPassword.message}
                  </p>
                )}
              </div>
              <Button 
                type="submit" 
                className="w-full" 
                disabled={isLoading}
                data-testid="button-signup-submit"
              >
                {isLoading ? "Creating account..." : "Create Account"}
              </Button>
            </form>
          )}

          <div className="text-center">
            <p className="text-sm text-muted-foreground">
              {mode === "signin" ? "Don't have an account?" : "Already have an account?"}
              <Button
                variant="link"
                className="ml-1 p-0 h-auto"
                onClick={handleModeToggle}
                data-testid="button-toggle-auth-mode"
              >
                {mode === "signin" ? "Sign up" : "Sign in"}
              </Button>
            </p>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/cart-sidebar.tsx.content:
import { Button } from "@/components/ui/button";
import { Sheet, SheetContent, SheetHeader, SheetTitle } from "@/components/ui/sheet";
import { Separator } from "@/components/ui/separator";
import { Minus, Plus, Trash2, ShoppingCart } from "lucide-react";
import { useCart } from "@/hooks/use-cart";
import { useAuth } from "@/hooks/use-auth";
import { useToast } from "@/hooks/use-toast";
import { Skeleton } from "@/components/ui/skeleton";
import { useLocation } from "wouter";

interface CartSidebarProps {
  isOpen: boolean;
  onClose: () => void;
}

export function CartSidebar({ isOpen, onClose }: CartSidebarProps) {
  const { cartItems, updateQuantity, removeFromCart, clearCart, isLoading } = useCart();
  const { user } = useAuth();
  const { toast } = useToast();
  const [, setLocation] = useLocation();

  const handleUpdateQuantity = async (productId: string, newQuantity: number) => {
    try {
      await updateQuantity(productId, newQuantity);
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to update quantity.",
        variant: "destructive",
      });
    }
  };

  const handleRemoveItem = async (productId: string) => {
    try {
      await removeFromCart(productId);
      toast({
        title: "Item removed",
        description: "Item has been removed from your cart.",
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to remove item.",
        variant: "destructive",
      });
    }
  };

  const handleClearCart = async () => {
    try {
      await clearCart();
      toast({
        title: "Cart cleared",
        description: "All items have been removed from your cart.",
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to clear cart.",
        variant: "destructive",
      });
    }
  };

  const calculateTotal = () => {
    if (!cartItems) return "â‚¹0";

    const total = cartItems.reduce((sum, item) => {
      const price = parseFloat(item.product.discountedPrice.replace(/[â‚¹,]/g, ''));
      return sum + (price * item.quantity);
    }, 0);

    return `â‚¹${total.toLocaleString()}`;
  };

  const parsePrice = (priceStr: string) => {
    return parseFloat(priceStr.replace(/[â‚¹,]/g, ''));
  };

  if (!user) {
    return (
      <Sheet open={isOpen} onOpenChange={onClose}>
        <SheetContent className="w-80 glass-dark">
          <SheetHeader>
            <SheetTitle data-testid="cart-title" className="text-gradient flex items-center gap-2">
            <ShoppingCart className="h-5 w-5 animate-pulse" />
            Shopping Cart
          </SheetTitle>
          </SheetHeader>
          <div className="flex-1 flex items-center justify-center py-12">
            <div className="text-center">
              <ShoppingCart className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
              <p className="text-muted-foreground mb-4" data-testid="text-signin-required">
                Please sign in to view your cart
              </p>
            </div>
          </div>
        </SheetContent>
      </Sheet>
    );
  }

  return (
    <Sheet open={isOpen} onOpenChange={onClose}>
      <SheetContent className="w-80 flex flex-col glass-dark">
        <SheetHeader>
          <SheetTitle data-testid="cart-title" className="text-gradient flex items-center gap-2">
            <ShoppingCart className="h-5 w-5 animate-pulse" />
            Shopping Cart
          </SheetTitle>
        </SheetHeader>

        <div className="flex-1 overflow-y-auto py-4">
          {isLoading ? (
            <div className="space-y-4">
              {Array.from({ length: 3 }).map((_, i) => (
                <div key={i} className="flex items-center space-x-3 p-3 bg-background rounded-lg">
                  <Skeleton className="w-12 h-12 rounded" />
                  <div className="flex-1 space-y-2">
                    <Skeleton className="h-4 w-3/4" />
                    <Skeleton className="h-3 w-1/2" />
                  </div>
                </div>
              ))}
            </div>
          ) : cartItems?.length === 0 ? (
            <div className="flex-1 flex items-center justify-center py-12">
              <div className="text-center">
                <ShoppingCart className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                <p className="text-muted-foreground" data-testid="text-empty-cart">
                  Your cart is empty
                </p>
              </div>
            </div>
          ) : (
            <div className="space-y-4">
              {cartItems?.map((item, index) => (
                <div 
                  key={item.id} 
                  className="flex items-center space-x-3 p-3 glass-dark rounded-lg hover:shadow-lg transition-all duration-300" 
                  data-testid={`cart-item-${item.productId}`}
                  style={{ animation: `fadeIn 0.3s ease-out ${index * 0.1}s both` }}
                >
                  <img
                    src={item.product.imgLink || "https://images.unsplash.com/photo-1583863788434-e58a36330cf0?ixlib=rb-4.0.3&auto=format&fit=crop&w=60&h=60"}
                    alt={item.product.productName}
                    className="w-12 h-12 object-cover rounded"
                    data-testid={`img-cart-item-${item.productId}`}
                  />
                  <div className="flex-1 min-w-0">
                    <h4 className="text-sm font-medium line-clamp-2 hover:text-primary transition-colors" data-testid={`text-item-name-${item.productId}`}>
                      {item.product.productName}
                    </h4>
                    <p className="text-xs text-gradient font-bold" data-testid={`text-item-price-${item.productId}`}>
                      {item.product.discountedPrice}
                    </p>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Button
                      variant="outline"
                      size="sm"
                      className="h-6 w-6 p-0 hover:scale-110 transition-transform"
                      onClick={() => handleUpdateQuantity(item.productId, item.quantity - 1)}
                      disabled={item.quantity <= 1}
                      data-testid={`button-decrease-${item.productId}`}
                    >
                      <Minus className="h-3 w-3" />
                    </Button>
                    <span className="text-sm w-8 text-center font-bold" data-testid={`text-quantity-${item.productId}`}>
                      {item.quantity}
                    </span>
                    <Button
                      variant="outline"
                      size="sm"
                      className="h-6 w-6 p-0 hover:scale-110 transition-transform"
                      onClick={() => handleUpdateQuantity(item.productId, item.quantity + 1)}
                      data-testid={`button-increase-${item.productId}`}
                    >
                      <Plus className="h-3 w-3" />
                    </Button>
                  </div>
                  <Button
                    variant="ghost"
                    size="sm"
                    className="text-destructive hover:text-destructive p-1 hover:scale-110 hover:bg-destructive/20 transition-all"
                    onClick={() => handleRemoveItem(item.productId)}
                    data-testid={`button-remove-${item.productId}`}
                  >
                    <Trash2 className="h-3 w-3" />
                  </Button>
                </div>
              ))}
            </div>
          )}
        </div>

        {cartItems && cartItems.length > 0 && (
          <div className="border-t border-border pt-4 space-y-4">
            <div className="flex items-center justify-between text-lg font-semibold glass-dark rounded-lg p-3">
              <span>Total:</span>
              <span data-testid="text-cart-total" className="text-gradient text-xl">{calculateTotal()}</span>
            </div>
            <Button 
              className="w-full btn-gradient text-white hover:scale-[1.02] transition-transform" 
              size="lg" 
              data-testid="button-checkout"
              onClick={() => {
                onClose();
                setLocation("/checkout");
              }}
            >
              Proceed to Checkout
            </Button>
            <Button 
              variant="outline" 
              className="w-full" 
              onClick={onClose}
              data-testid="button-continue-shopping"
            >
              Continue Shopping
            </Button>
            <Separator />
            <Button 
              variant="destructive" 
              className="w-full hover:scale-[1.02] transition-transform" 
              onClick={handleClearCart}
              data-testid="button-clear-cart"
            >
              Clear Cart
            </Button>
          </div>
        )}
      </SheetContent>
    </Sheet>
  );
}

client/src/components/header.tsx.content:
import { useState } from "react";
import { Link } from "wouter";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuSeparator, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { ShoppingBag, Search, ShoppingCart, User, Moon, Sun, LogOut, Heart, UserCircle, Package, Settings, Sparkles } from "lucide-react";
import { useAuth } from "@/hooks/use-auth";
import { useCart } from "@/hooks/use-cart";
import { useTheme } from "@/components/theme-provider";
import { useToast } from "@/hooks/use-toast";

interface HeaderProps {
  onSearch: (value: string) => void;
  onCartToggle: () => void;
  onAuthToggle: () => void;
  searchValue?: string;
}

export function Header({ onSearch, onCartToggle, onAuthToggle, searchValue = "" }: HeaderProps) {
  const [searchInput, setSearchInput] = useState(searchValue);
  const { user, signOut } = useAuth();
  const { cartItems } = useCart();
  const { theme, toggleTheme } = useTheme();
  const { toast } = useToast();

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    onSearch(searchInput);
  };

  const handleSignOut = async () => {
    try {
      await signOut();
      toast({
        title: "Signed out",
        description: "You have been signed out successfully.",
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to sign out.",
        variant: "destructive",
      });
    }
  };

  const cartItemCount = cartItems?.reduce((total, item) => total + item.quantity, 0) || 0;

  return (
    <header className="sticky top-0 z-50 w-full glass-dark border-b border-border/50 shadow-lg transition-all duration-300">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex h-16 items-center justify-between">
          {/* Logo */}
          <Link href="/shop">
            <div className="flex items-center space-x-2 cursor-pointer hover:scale-105 transition-transform duration-300" data-testid="logo-link">
              <div className="btn-gradient text-white rounded-lg p-2 shadow-lg">
                <ShoppingBag className="h-5 w-5 animate-pulse" />
              </div>
              <span className="text-2xl font-bold text-gradient">ShopHub</span>
            </div>
          </Link>

          {/* Search Bar - Hidden on mobile, shown on md+ */}
          <div className="hidden md:flex flex-1 max-w-2xl mx-8">
            <div className="relative w-full">
              <form onSubmit={handleSearch} className="flex items-center gap-2">
                <div className="relative flex-1">
                  <Input
                    id="search-input"
                    type="text"
                    placeholder="Search products with AI..."
                    value={searchInput}
                    onChange={(e) => setSearchInput(e.target.value)}
                    className="w-full pl-10 pr-4 h-10 glass border-input focus:border-purple-500 transition-all duration-300 focus:scale-[1.02] focus:shadow-lg"
                    data-testid="input-search-desktop"
                  />
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground pointer-events-none animate-pulse" />
                </div>
                <Button
                  type="submit"
                  size="default"
                  className="ai-search-button h-10 px-4 rounded-md flex items-center justify-center whitespace-nowrap"
                  data-testid="button-search-desktop"
                >
                  <Sparkles className="h-4 w-4 mr-1.5 text-white sparkle-icon" />
                  <span className="text-white font-medium">AI Search</span>
                </Button>
              </form>
            </div>
          </div>

          {/* Navigation */}
          <nav className="flex items-center space-x-4">
            {/* Theme Toggle */}
            <Button
              variant="ghost"
              size="sm"
              onClick={toggleTheme}
              className="hover:scale-110 transition-transform duration-300"
              data-testid="button-theme-toggle"
            >
              {theme === "dark" ? <Sun className="h-4 w-4 animate-spin-slow" /> : <Moon className="h-4 w-4 animate-pulse" />}
            </Button>

            {/* Cart */}
            <Button
              variant="ghost"
              size="sm"
              className="relative hover:scale-110 transition-transform duration-300"
              onClick={onCartToggle}
              data-testid="button-cart"
            >
              <ShoppingCart className="h-4 w-4" />
              {cartItemCount > 0 && (
                <span className="absolute -top-1 -right-1 bg-gradient-to-r from-red-500 to-pink-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center badge-animate" data-testid="cart-count">
                  {cartItemCount}
