          </div>
          <Button
            type="submit"
            size="default"
            className="ai-search-button h-10 px-3 rounded-md flex items-center justify-center text-xs whitespace-nowrap"
            data-testid="button-search-mobile"
          >
            <Sparkles className="h-3 w-3 mr-1 text-white sparkle-icon" />
            <span className="text-white">AI Search</span>
          </Button>
        </form>
      </div>
    </header>
  );
}

client/src/components/pagination.tsx.content:
import { Button } from "@/components/ui/button";
import { ChevronLeft, ChevronRight } from "lucide-react";

interface PaginationProps {
  currentPage: number;
  totalPages: number;
  total: number;
  limit: number;
  onPageChange: (page: number) => void;
}

export function Pagination({ currentPage, totalPages, total, limit, onPageChange }: PaginationProps) {
  const getVisiblePages = () => {
    const delta = 2;
    const range = [];
    const rangeWithDots = [];

    for (let i = Math.max(2, currentPage - delta); i <= Math.min(totalPages - 1, currentPage + delta); i++) {
      range.push(i);
    }

    if (currentPage - delta > 2) {
      rangeWithDots.push(1, '...');
    } else {
      rangeWithDots.push(1);
    }

    rangeWithDots.push(...range);

    if (currentPage + delta < totalPages - 1) {
      rangeWithDots.push('...', totalPages);
    } else if (totalPages > 1) {
      rangeWithDots.push(totalPages);
    }

    return rangeWithDots;
  };

  const startItem = (currentPage - 1) * limit + 1;
  const endItem = Math.min(currentPage * limit, total);

  return (
    <div className="flex flex-col sm:flex-row items-center justify-between gap-4">
      <p className="text-sm text-muted-foreground" data-testid="pagination-info">
        Showing <span className="font-medium">{startItem}</span> to <span className="font-medium">{endItem}</span> of{' '}
        <span className="font-medium">{total}</span> results
      </p>

      <nav className="flex items-center space-x-2">
        <Button
          variant="outline"
          size="sm"
          onClick={() => onPageChange(currentPage - 1)}
          disabled={currentPage <= 1}
          data-testid="button-previous-page"
        >
          <ChevronLeft className="h-4 w-4 mr-1" />
          Previous
        </Button>

        <div className="flex items-center space-x-1">
          {getVisiblePages().map((page, index) => (
            page === '...' ? (
              <span key={`ellipsis-${index}`} className="px-2 text-muted-foreground">...</span>
            ) : (
              <Button
                key={`page-${page}`}
                variant={currentPage === page ? "default" : "outline"}
                size="sm"
                onClick={() => onPageChange(page as number)}
                data-testid={`button-page-${page}`}
              >
                {page}
              </Button>
            )
          ))}
        </div>

        <Button
          variant="outline"
          size="sm"
          onClick={() => onPageChange(currentPage + 1)}
          disabled={currentPage >= totalPages}
          data-testid="button-next-page"
        >
          Next
          <ChevronRight className="h-4 w-4 ml-1" />
        </Button>
      </nav>
    </div>
  );
}

client/src/components/product-card.tsx.content:
import { useState } from "react";
import { Link } from "wouter";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Star, Heart, Plus, ShoppingCart } from "lucide-react";
import { Product } from "@shared/schema";
import { useAuth } from "@/hooks/use-auth";
import { useCart } from "@/hooks/use-cart";
import { useWishlist } from "@/hooks/use-wishlist";
import { useToast } from "@/hooks/use-toast";

interface ProductCardProps {
  product: Product;
  viewMode?: "grid" | "list";
}

export function ProductCard({ product, viewMode = "grid" }: ProductCardProps) {
  const [imageError, setImageError] = useState(false);
  const { user } = useAuth();
  const { addToCart, isLoading } = useCart();
  const { toggleWishlist, isInWishlist, isToggling } = useWishlist();
  const { toast } = useToast();

  const renderStars = (rating: number) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;

    for (let i = 0; i < fullStars; i++) {
      stars.push(<Star key={i} className="h-3 w-3 fill-yellow-400 text-yellow-400" />);
    }

    if (hasHalfStar) {
      stars.push(<Star key="half" className="h-3 w-3 fill-yellow-400/50 text-yellow-400" />);
    }

    const emptyStars = 5 - Math.ceil(rating);
    for (let i = 0; i < emptyStars; i++) {
      stars.push(<Star key={`empty-${i}`} className="h-3 w-3 text-muted-foreground" />);
    }

    return stars;
  };

  const handleAddToCart = async (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();

    if (!user) {
      toast({
        title: "Sign in required",
        description: "Please sign in to add items to your cart.",
        variant: "destructive",
      });
      return;
    }

    try {
      await addToCart({ productId: product.id, quantity: 1 });
      toast({
        title: "Added to cart",
        description: `${product.productName} has been added to your cart.`,
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to add product to cart.",
        variant: "destructive",
      });
    }
  };

  const handleWishlistToggle = async (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();

    if (!user) {
      toast({
        title: "Sign in required",
        description: "Please sign in to add items to your wishlist.",
        variant: "destructive",
      });
      return;
    }

    try {
      await toggleWishlist(product.id);
      const isNowInWishlist = !isInWishlist(product.id);
      toast({
        title: isNowInWishlist ? "Added to wishlist" : "Removed from wishlist",
        description: isNowInWishlist 
          ? `${product.productName} has been added to your wishlist.`
          : `${product.productName} has been removed from your wishlist.`,
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to update wishlist.",
        variant: "destructive",
      });
    }
  };

  const getImageSrc = () => {
    if (imageError || !product.imgLink) {
      return "https://images.unsplash.com/photo-1583863788434-e58a36330cf0?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=300";
    }
    // Handle both external URLs and local generated images
    if (product.imgLink.startsWith('http://') || product.imgLink.startsWith('https://')) {
      return product.imgLink;
    }
    // For local generated images
    return product.imgLink;
  };

  if (viewMode === "list") {
    return (
      <Card className="group glass-dark card-hover transition-all duration-300 hover:shadow-xl" data-testid={`card-product-${product.id}`}>
        <Link href={`/product/${product.id}`}>
          <CardContent className="p-4">
            <div className="flex gap-4">
              <div className="relative w-24 h-24 flex-shrink-0">
                <img
                  src={getImageSrc()}
                  alt={product.productName}
                  className="w-full h-full object-cover rounded-lg"
                  onError={() => setImageError(true)}
                  data-testid={`img-product-${product.id}`}
                />
                {product.discountPercentage && (
                  <Badge variant="destructive" className="absolute top-1 left-1 text-xs">
                    {product.discountPercentage} OFF
                  </Badge>
                )}
              </div>

              <div className="flex-1 min-w-0">
                <div className="flex items-start justify-between mb-2">
                  <div className="flex items-center space-x-1">
                    {product.rating && (
                      <>
                        <div className="flex" data-testid={`rating-${product.id}`}>
                          {renderStars(parseFloat(product.rating.toString()))}
                        </div>
                        <span className="text-xs text-muted-foreground">
                          ({product.ratingCount})
                        </span>
                      </>
                    )}
                  </div>
                  <Button 
                    variant="ghost" 
                    size="sm" 
                    onClick={handleWishlistToggle}
                    disabled={isToggling}
                    data-testid={`button-wishlist-${product.id}`}
                  >
                    <Heart className={`h-4 w-4 ${isInWishlist(product.id) ? 'fill-red-500 text-red-500' : ''}`} />
                  </Button>
                </div>

                <h3 className="font-medium text-sm mb-1 line-clamp-2 group-hover:text-primary transition-colors" data-testid={`text-name-${product.id}`}>
                  {product.productName}
                </h3>

                <p className="text-xs text-muted-foreground mb-2 line-clamp-1" data-testid={`text-category-${product.id}`}>
                  {product.category}
                </p>

                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-2">
                    <span className="text-lg font-bold text-primary" data-testid={`text-price-${product.id}`}>
                      {product.discountedPrice}
                    </span>
                    <span className="text-sm text-muted-foreground line-through">
                      {product.actualPrice}
                    </span>
                  </div>
                  <Button
                    size="sm"
                    onClick={handleAddToCart}
                    disabled={isLoading}
                    data-testid={`button-add-cart-${product.id}`}
                  >
                    <ShoppingCart className="h-4 w-4 mr-1" />
                    Add
                  </Button>
                </div>
              </div>
            </div>
          </CardContent>
        </Link>
      </Card>
    );
  }

  return (
    <Card className="group glass-dark card-hover transition-all duration-300 overflow-hidden" data-testid={`card-product-${product.id}`}>
      <Link href={`/product/${product.id}`}>
        <div className="relative overflow-hidden">
          <img
            src={getImageSrc()}
            alt={product.productName}
            className="w-full h-48 object-cover transition-transform duration-500 group-hover:scale-110"
            onError={() => setImageError(true)}
            data-testid={`img-product-${product.id}`}
          />
          <div className="absolute inset-0 bg-gradient-to-t from-black/60 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
          {product.discountPercentage && (
            <Badge variant="destructive" className="absolute top-2 left-2 z-10 badge-animate">
              {product.discountPercentage} OFF
            </Badge>
          )}
          <Button
            variant="ghost"
            size="sm"
            className="absolute top-2 right-2 opacity-0 group-hover:opacity-100 transition-all duration-300 transform group-hover:scale-110 glass z-10"
            onClick={handleWishlistToggle}
            disabled={isToggling}
            data-testid={`button-wishlist-${product.id}`}
          >
            <Heart className={`h-4 w-4 ${isInWishlist(product.id) ? 'fill-red-500 text-red-500 animate-pulse' : ''}`} />
          </Button>
        </div>

        <CardContent className="p-4">
          <div className="flex items-center justify-between mb-2">
            <div className="flex items-center space-x-1">
              {product.rating && (
                <>
                  <div className="flex" data-testid={`rating-${product.id}`}>
                    {renderStars(parseFloat(product.rating.toString()))}
                  </div>
                  <span className="text-xs text-muted-foreground">
                    ({product.ratingCount})
                  </span>
                </>
              )}
            </div>
          </div>

          <h3 className="font-medium text-sm mb-2 line-clamp-2 group-hover:text-primary transition-colors duration-300" data-testid={`text-name-${product.id}`}>
            {product.productName}
          </h3>

          <p className="text-xs text-muted-foreground mb-3 line-clamp-2" data-testid={`text-category-${product.id}`}>
            {product.category}
          </p>

          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <span className="text-lg font-bold text-gradient" data-testid={`text-price-${product.id}`}>
                {product.discountedPrice}
              </span>
              <span className="text-sm text-muted-foreground line-through">
                {product.actualPrice}
              </span>
            </div>
            <Button
              size="sm"
              onClick={handleAddToCart}
              disabled={isLoading}
              className="btn-gradient text-white hover:scale-105 transform transition-all duration-300"
              data-testid={`button-add-cart-${product.id}`}
            >
              <Plus className="h-4 w-4" />
            </Button>
          </div>
        </CardContent>
      </Link>
    </Card>
  );
}

client/src/components/protected-route.ts.content:
import { useAuth } from "@/hooks/use-auth";
import { Redirect } from "wouter";

interface ProtectedRouteProps {
  children: React.ReactNode;
}

export function ProtectedRoute({ children }: ProtectedRouteProps) {
  const { user, isLoading } = useAuth();

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">Loading...</p>
        </div>
      </div>
    );
  }

  if (!user) {
    return <Redirect to="/login" />;
  }

  return <>{children}</>;
}

client/src/components/sidebar-filters.ts.content:
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Checkbox } from "@/components/ui/checkbox";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Sheet, SheetContent, SheetHeader, SheetTitle } from "@/components/ui/sheet";
import { Star, X } from "lucide-react";
import React from "react";
import { useQuery } from "@tanstack/react-query";
import { useState } from "react";

interface SidebarFiltersProps {
  onCategoryChange: (category: string) => void;
  onPriceChange: (min: string, max: string) => void;
  onRatingChange: (rating: string) => void;
  onClearFilters: () => void;
  selectedCategory: string;
  selectedRating: string;
  priceRange: { min: string; max: string };
  isOpen: boolean;
  onClose: () => void;
}

export function SidebarFilters({ 
  onCategoryChange, 
  onPriceChange,
  onRatingChange,
  onClearFilters, 
  selectedCategory,
  selectedRating,
  priceRange,
  isOpen,
  onClose 
}: SidebarFiltersProps) {
  const [priceMin, setPriceMin] = useState(priceRange?.min || "");
  const [priceMax, setPriceMax] = useState(priceRange?.max || "");

  const { data: categories } = useQuery({
    queryKey: ['/api/categories'],
  });

  const renderStars = (count: number) => {
    const stars = [];
    for (let i = 0; i < count; i++) {
      stars.push(<Star key={i} className="h-3 w-3 fill-yellow-400 text-yellow-400" />);
    }
    for (let i = count; i < 5; i++) {
      stars.push(<Star key={i} className="h-3 w-3 text-muted-foreground" />);
    }
    return stars;
  };

  const handleCategoryChange = (category: string, checked: boolean) => {
    if (checked) {
      onCategoryChange(category);
    } else if (selectedCategory === category) {
      onCategoryChange("");
    }
  };

  const applyPriceFilter = () => {
    onPriceChange(priceMin, priceMax);
  };

  const handleRatingChange = (value: string) => {
    onRatingChange(value);
  };

  // Update local state when props change
  React.useEffect(() => {
    setPriceMin(priceRange.min);
    setPriceMax(priceRange.max);
  }, [priceRange]);

  const FilterContent = () => (
    <div className="space-y-6">
      {/* Categories */}
      <div>
        <h3 className="text-sm font-medium mb-3 text-muted-foreground">Categories</h3>
        <div className="space-y-2 max-h-64 overflow-y-auto">
          {categories?.map((category: string) => (
            <div key={category} className="flex items-center space-x-2">
              <Checkbox
                id={`category-${category}`}
                checked={selectedCategory === category}
                onCheckedChange={(checked) => handleCategoryChange(category, checked as boolean)}
                data-testid={`checkbox-category-${category}`}
              />
              <Label htmlFor={`category-${category}`} className="text-sm cursor-pointer truncate" title={category}>
                {category.replace(/&/g, ' & ').replace(/([A-Z])/g, ' $1').trim().replace(/\s+/g, ' ')}
              </Label>
            </div>
          ))}
        </div>
      </div>

      {/* Price Range */}
      <div>
        <h3 className="text-sm font-medium mb-3 text-muted-foreground">Price Range</h3>
        <div className="space-y-3">
          <div className="flex space-x-2">
            <Input
              type="number"
              placeholder="Min"
              value={priceMin}
              onChange={(e) => setPriceMin(e.target.value)}
              className="text-sm"
              data-testid="input-price-min"
            />
            <Input
              type="number"
              placeholder="Max"
              value={priceMax}
              onChange={(e) => setPriceMax(e.target.value)}
              className="text-sm"
              data-testid="input-price-max"
            />
          </div>
          <Button 
            onClick={applyPriceFilter} 
            className="w-full" 
            size="sm"
            data-testid="button-apply-price"
          >
            Apply
          </Button>
        </div>
      </div>

      {/* Rating */}
      <div>
        <h3 className="text-sm font-medium mb-3 text-muted-foreground">Rating</h3>
        <RadioGroup 
          className="space-y-2" 
          value={selectedRating} 
          onValueChange={handleRatingChange}
        >
          <div className="flex items-center space-x-2">
            <RadioGroupItem value="5" id="rating-5" />
            <Label htmlFor="rating-5" className="flex items-center cursor-pointer">
              <div className="flex mr-2">
                {renderStars(5)}
              </div>
              <span className="text-sm">5 stars</span>
            </Label>
          </div>
          <div className="flex items-center space-x-2">
            <RadioGroupItem value="4" id="rating-4" />
            <Label htmlFor="rating-4" className="flex items-center cursor-pointer">
              <div className="flex mr-2">
                {renderStars(4)}
              </div>
              <span className="text-sm">4+ stars</span>
            </Label>
          </div>
          <div className="flex items-center space-x-2">
            <RadioGroupItem value="3" id="rating-3" />
            <Label htmlFor="rating-3" className="flex items-center cursor-pointer">
              <div className="flex mr-2">
                {renderStars(3)}
              </div>
              <span className="text-sm">3+ stars</span>
            </Label>
          </div>
        </RadioGroup>
      </div>

      {/* Clear Filters */}
      <Button 
        variant="outline" 
        onClick={onClearFilters} 
        className="w-full"
        data-testid="button-clear-filters"
      >
        Clear All Filters
      </Button>
    </div>
  );

  return (
    <>
      {/* Desktop Sidebar */}
      <aside className="hidden lg:block w-64 bg-card border-r border-border">
        <div className="p-6">
          <h2 className="text-lg font-semibold mb-4">Filters</h2>
          <FilterContent />
        </div>
      </aside>

      {/* Mobile Sheet */}
      <Sheet open={isOpen} onOpenChange={onClose}>
        <SheetContent side="left" className="w-80">
          <SheetHeader>
            <SheetTitle>Filters</SheetTitle>
          </SheetHeader>
          <div className="mt-6">
            <FilterContent />
          </div>
        </SheetContent>
      </Sheet>
    </>
  );
}

