  const handleRatingFilter = (value: string) => {
    setRating(value);
    setPage(1);
  };

  const clearFilters = () => {
    setSearch("");
    setCategory("");
    setSortBy("relevance");
    setPriceRange({ min: "", max: "" });
    setRating("");
    setPage(1);
  };

  return (
    <div className="min-h-screen bg-background text-foreground page-transition">
      <Header 
        onSearch={handleSearch}
        onCartToggle={() => setShowCart(true)}
        onAuthToggle={() => setShowAuth(true)}
        searchValue={search}
      />

      {/* Hero Section */}
      {!search && !category && page === 1 && (
        <section className="relative overflow-hidden px-4 py-12 mb-8">
          <div className="animated-gradient absolute inset-0 opacity-10"></div>
          <div className="relative max-w-7xl mx-auto text-center">
            <h1 className="text-5xl md:text-6xl font-bold mb-6 animate-fadeIn" 
                style={{animation: "fadeIn 0.8s ease-out"}}>
              Discover Amazing Products
            </h1>
            <p className="text-xl text-muted-foreground mb-8 max-w-2xl mx-auto" 
               style={{animation: "fadeIn 0.8s ease-out 0.2s both"}}>
              Shop the latest trends with AI-powered search and personalized recommendations
            </p>
            <div className="flex gap-4 justify-center" 
                 style={{animation: "fadeIn 0.8s ease-out 0.4s both"}}>
              <Button 
                className="btn-gradient text-white px-8 py-6 text-lg"
                onClick={() => document.getElementById('search-input')?.focus()}
              >
                Start Shopping
              </Button>
              <Button 
                variant="outline" 
                className="px-8 py-6 text-lg glass-dark hover:scale-105 transition-transform"
                onClick={() => setShowMobileFilters(true)}
              >
                Browse Categories
              </Button>
            </div>
          </div>
        </section>
      )}

      <div className="flex min-h-screen">
        <SidebarFilters
          onCategoryChange={handleCategoryFilter}
          onPriceChange={handlePriceFilter}
          onRatingChange={handleRatingFilter}
          onClearFilters={clearFilters}
          selectedCategory={category}
          selectedRating={rating}
          priceRange={priceRange}
          isOpen={showMobileFilters}
          onClose={() => setShowMobileFilters(false)}
        />

        <main className="flex-1 p-4 lg:p-6 page-transition">
          {/* Toolbar */}
          <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4 mb-6">
            <div className="page-transition">
              <h1 className="text-3xl font-bold mb-2" data-testid="page-title">
                {search ? `Search Results for "${search}"` : 
                 category ? category : 
                 'All Products'}
              </h1>
              <p className="text-muted-foreground" data-testid="products-count">
                {productsData ? 
                  `Showing ${((page - 1) * 20) + 1}-${Math.min(page * 20, productsData.pagination.total)} of ${productsData.pagination.total} products` :
                  'Loading...'
                }
              </p>
            </div>

            <div className="flex items-center space-x-4">
              {/* Mobile Filter Toggle */}
              <Button
                variant="outline"
                className="lg:hidden"
                onClick={() => setShowMobileFilters(true)}
                data-testid="button-mobile-filters"
              >
                <Filter className="h-4 w-4 mr-2" />
                Filters
              </Button>

              {/* Sort Dropdown */}
              <Select value={sortBy} onValueChange={handleSortChange}>
                <SelectTrigger className="w-48" data-testid="select-sort">
                  <SelectValue placeholder="Sort by" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="relevance">Sort by Relevance</SelectItem>
                  <SelectItem value="price-low">Price: Low to High</SelectItem>
                  <SelectItem value="price-high">Price: High to Low</SelectItem>
                  <SelectItem value="rating-high">Rating: High to Low</SelectItem>
                  <SelectItem value="rating-low">Rating: Low to High</SelectItem>
                  <SelectItem value="newest">Newest Arrivals</SelectItem>
                </SelectContent>
              </Select>

              {/* View Toggle */}
              <div className="flex rounded-lg border border-border overflow-hidden">
                <Button
                  variant={viewMode === "grid" ? "default" : "ghost"}
                  size="sm"
                  onClick={() => setViewMode("grid")}
                  data-testid="button-grid-view"
                >
                  <Grid className="h-4 w-4" />
                </Button>
                <Button
                  variant={viewMode === "list" ? "default" : "ghost"}
                  size="sm"
                  onClick={() => setViewMode("list")}
                  data-testid="button-list-view"
                >
                  <List className="h-4 w-4" />
                </Button>
              </div>
            </div>
          </div>

          {/* Products Grid */}
          {isLoading ? (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6 mb-8">
              {Array.from({ length: 20 }).map((_, i) => (
                <div key={i} className="space-y-4 glass-dark rounded-lg p-4 skeleton">
                  <Skeleton className="h-48 w-full rounded-lg" />
                  <Skeleton className="h-4 w-3/4" />
                  <Skeleton className="h-4 w-1/2" />
                  <Skeleton className="h-6 w-1/4" />
                </div>
              ))}
            </div>
          ) : productsData?.products.length === 0 ? (
            <div className="text-center py-12" data-testid="empty-state">
              <p className="text-muted-foreground text-lg">No products found</p>
              <Button onClick={clearFilters} className="mt-4" data-testid="button-clear-filters">
                Clear Filters
              </Button>
            </div>
          ) : (
            <div className={`grid gap-6 mb-8 ${
              viewMode === "grid" 
                ? "grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4" 
                : "grid-cols-1"
            }`}>
              {productsData?.products.map((product, index) => (
                <div
                  key={product.id}
                  style={{
                    animation: `fadeIn 0.5s ease-out ${index * 0.05}s both`
                  }}
                >
                  <ProductCard
                    product={product}
                    viewMode={viewMode}
                  />
                </div>
              ))}
            </div>
          )}

          {/* Pagination */}
          {productsData && productsData.pagination.totalPages > 1 && (
            <Pagination
              currentPage={page}
              totalPages={productsData.pagination.totalPages}
              total={productsData.pagination.total}
              limit={20}
              onPageChange={setPage}
            />
          )}
        </main>
      </div>

      <CartSidebar 
        isOpen={showCart} 
        onClose={() => setShowCart(false)} 
      />

      <AuthModal 
        isOpen={showAuth} 
        onClose={() => setShowAuth(false)} 
      />
    </div>
  );
}

client/src/pages/login.tsx.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { ShoppingBag } from "lucide-react";
import { useAuth } from "@/hooks/use-auth";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { signInSchema, signUpSchema } from "@shared/schema";
import { z } from "zod";
import { useToast } from "@/hooks/use-toast";

type SignInData = z.infer<typeof signInSchema>;
type SignUpData = z.infer<typeof signUpSchema>;

export default function Login() {
  const [mode, setMode] = useState<"signin" | "signup">("signin");
  const [, setLocation] = useLocation();
  const { signIn, signUp, isLoading } = useAuth();
  const { toast } = useToast();

  const signInForm = useForm<SignInData>({
    resolver: zodResolver(signInSchema),
    defaultValues: {
      email: "",
      password: "",
    },
  });

  const signUpForm = useForm<SignUpData>({
    resolver: zodResolver(signUpSchema),
    defaultValues: {
      email: "",
      password: "",
      confirmPassword: "",
      name: "",
    },
  });

  const handleSignIn = async (data: SignInData) => {
    try {
      await signIn(data);
      toast({
        title: "Welcome back!",
        description: "You have been signed in successfully.",
      });
      setLocation("/shop");
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to sign in",
        variant: "destructive",
      });
    }
  };

  const handleSignUp = async (data: SignUpData) => {
    try {
      await signUp(data);
      toast({
        title: "Account created!",
        description: "Welcome to ShopHub!",
      });
      setLocation("/shop");
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to create account",
        variant: "destructive",
      });
    }
  };

  return (
    <div className="min-h-screen bg-background flex items-center justify-center p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="text-center">
          <div className="flex items-center justify-center space-x-2 mb-4">
            <div className="bg-primary text-primary-foreground rounded-lg p-2">
              <ShoppingBag className="h-6 w-6" />
            </div>
            <span className="text-2xl font-bold">ShopHub</span>
          </div>
          <CardTitle data-testid="auth-title">
            {mode === "signin" ? "Sign In" : "Create Account"}
          </CardTitle>
          <CardDescription>
            {mode === "signin" 
              ? "Welcome back! Please sign in to your account."
              : "Join ShopHub to start shopping amazing products."
            }
          </CardDescription>
        </CardHeader>
        <CardContent>
          {mode === "signin" ? (
            <form onSubmit={signInForm.handleSubmit(handleSignIn)} className="space-y-4">
              <div>
                <Label htmlFor="email">Email</Label>
                <Input
                  id="email"
                  type="email"
                  {...signInForm.register("email")}
                  data-testid="input-email"
                />
                {signInForm.formState.errors.email && (
                  <p className="text-sm text-destructive mt-1">
                    {signInForm.formState.errors.email.message}
                  </p>
                )}
              </div>
              <div>
                <Label htmlFor="password">Password</Label>
                <Input
                  id="password"
                  type="password"
                  {...signInForm.register("password")}
                  data-testid="input-password"
                />
                {signInForm.formState.errors.password && (
                  <p className="text-sm text-destructive mt-1">
                    {signInForm.formState.errors.password.message}
                  </p>
                )}
              </div>
              <Button 
                type="submit" 
                className="w-full" 
                disabled={isLoading}
                data-testid="button-signin"
              >
                {isLoading ? "Signing in..." : "Sign In"}
              </Button>
            </form>
          ) : (
            <form onSubmit={signUpForm.handleSubmit(handleSignUp)} className="space-y-4">
              <div>
                <Label htmlFor="name">Full Name</Label>
                <Input
                  id="name"
                  {...signUpForm.register("name")}
                  data-testid="input-name"
                />
                {signUpForm.formState.errors.name && (
                  <p className="text-sm text-destructive mt-1">
                    {signUpForm.formState.errors.name.message}
                  </p>
                )}
              </div>
              <div>
                <Label htmlFor="signup-email">Email</Label>
                <Input
                  id="signup-email"
                  type="email"
                  {...signUpForm.register("email")}
                  data-testid="input-signup-email"
                />
                {signUpForm.formState.errors.email && (
                  <p className="text-sm text-destructive mt-1">
                    {signUpForm.formState.errors.email.message}
                  </p>
                )}
              </div>
              <div>
                <Label htmlFor="signup-password">Password</Label>
                <Input
                  id="signup-password"
                  type="password"
                  {...signUpForm.register("password")}
                  data-testid="input-signup-password"
                />
                {signUpForm.formState.errors.password && (
                  <p className="text-sm text-destructive mt-1">
                    {signUpForm.formState.errors.password.message}
                  </p>
                )}
              </div>
              <div>
                <Label htmlFor="confirm-password">Confirm Password</Label>
                <Input
                  id="confirm-password"
                  type="password"
                  {...signUpForm.register("confirmPassword")}
                  data-testid="input-confirm-password"
                />
                {signUpForm.formState.errors.confirmPassword && (
                  <p className="text-sm text-destructive mt-1">
                    {signUpForm.formState.errors.confirmPassword.message}
                  </p>
                )}
              </div>
              <Button 
                type="submit" 
                className="w-full" 
                disabled={isLoading}
                data-testid="button-signup"
              >
                {isLoading ? "Creating account..." : "Create Account"}
              </Button>
            </form>
          )}

          <div className="mt-6 text-center">
            <p className="text-sm text-muted-foreground">
              {mode === "signin" ? "Don't have an account?" : "Already have an account?"}
              <Button
                variant="link"
                className="ml-1 p-0 h-auto"
                onClick={() => setMode(mode === "signin" ? "signup" : "signin")}
                data-testid="button-toggle-mode"
              >
                {mode === "signin" ? "Sign up" : "Sign in"}
              </Button>
            </p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

