import { useQuery } from "@tanstack/react-query";
import { Package, Download, ShoppingCart } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import { Link } from "wouter";
import jsPDF from "jspdf";
import type { Order, OrderItem, Product } from "@shared/schema";

export default function Orders() {
  const { data: orders = [], isLoading } = useQuery<(Order & { items: (OrderItem & { product: Product })[] })[]>({
    queryKey: ["/api/orders"]
  });

  const downloadInvoice = (order: Order & { items: (OrderItem & { product: Product })[] }) => {
    const pdf = new jsPDF();

    // Header
    pdf.setFontSize(20);
    pdf.text("Invoice", 105, 20, { align: "center" });

    pdf.setFontSize(12);
    pdf.text(`Order ID: ${order.id}`, 20, 40);
    pdf.text(`Date: ${order.createdAt ? new Date(order.createdAt).toLocaleDateString() : 'N/A'}`, 20, 50);
    pdf.text(`Status: ${order.status}`, 20, 60);

    // Items table header
    pdf.setFontSize(10);
    pdf.text("Product", 20, 80);
    pdf.text("Quantity", 120, 80);
    pdf.text("Price", 150, 80);
    pdf.text("Total", 180, 80);

    // Items
    let yPosition = 90;
    order.items.forEach((item) => {
      const price = parseFloat(item.price.replace(/[^\d.]/g, ''));
      const total = parseFloat(item.totalPrice.replace(/[^\d.]/g, ''));

      // Truncate long product names
      const productName = item.productName.length > 50 
        ? item.productName.substring(0, 50) + "..." 
        : item.productName;

      pdf.text(productName, 20, yPosition);
      pdf.text(item.quantity.toString(), 120, yPosition);
      pdf.text(`â‚¹${price.toFixed(2)}`, 150, yPosition);
      pdf.text(`â‚¹${total.toFixed(2)}`, 180, yPosition);

      yPosition += 10;

      // Add new page if needed
      if (yPosition > 270) {
        pdf.addPage();
        yPosition = 20;
      }
    });

    // Total
    pdf.setFontSize(12);
    pdf.text(`Total Amount: â‚¹${order.totalAmount}`, 180, yPosition + 20, { align: "right" });

    // Save PDF
    pdf.save(`invoice_${order.id}.pdf`);
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-gray-600 dark:text-gray-400">Loading orders...</p>
        </div>
      </div>
    );
  }

  if (orders.length === 0) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="bg-white dark:bg-gray-800 rounded-full p-6 w-32 h-32 mx-auto mb-6 flex items-center justify-center">
            <Package className="w-16 h-16 text-gray-400" />
          </div>
          <h2 className="text-3xl font-bold mb-4 text-gray-900 dark:text-gray-100">No Orders Yet</h2>
          <p className="text-gray-600 dark:text-gray-400 mb-8 max-w-md mx-auto">
            You haven't placed any orders yet. Start shopping to see your orders here!
          </p>
          <Link href="/">
            <Button size="lg" data-testid="button-start-shopping">
              <ShoppingCart className="w-5 h-5 mr-2" />
              Start Shopping
            </Button>
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      <div className="container mx-auto px-4 py-8">
        <div className="max-w-6xl mx-auto">
          <h1 className="text-4xl font-bold mb-8 text-gray-900 dark:text-gray-100">My Orders</h1>

        <div className="space-y-6">
          {orders.map((order) => (
            <Card key={order.id} className="bg-white dark:bg-gray-800 border-gray-200 dark:border-gray-700" data-testid={`order-${order.id}`}>
              <CardHeader>
                <div className="flex justify-between items-start">
                  <div>
                    <h2 className="text-lg font-semibold text-gray-900 dark:text-gray-100 mb-2">Order #{order.id.slice(0, 8)}</h2>
                    <p className="text-sm text-gray-600 dark:text-gray-300" data-testid={`text-order-date-${order.id}`}>
                    Placed on {order.createdAt ? new Date(order.createdAt).toLocaleDateString('en-US', { 
                      year: 'numeric', 
                      month: 'long', 
                      day: 'numeric' 
                    }) : 'N/A'}
                    </p>
                    <p className="text-sm mt-1">
                      <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800 dark:bg-green-900/50 dark:text-green-300">
                      {order.status}
                      </span>
                    </p>
                  </div>
                  <div className="text-right">
                    <p className="text-xl font-bold text-gray-900 dark:text-gray-100 mb-2" data-testid={`text-order-total-${order.id}`}>â‚¹{order.totalAmount}</p>
                    <Button 
                      size="sm" 
                      variant="outline"
                      className="border-gray-300 dark:border-gray-600"
                      onClick={() => downloadInvoice(order)}
                      data-testid={`button-download-invoice-${order.id}`}
                    >
                      <Download className="w-4 h-4 mr-2" />
                      Download Invoice
                    </Button>
                  </div>
                </div>
              </CardHeader>
              <CardContent>
                <Separator className="mb-4" />
                <h3 className="font-semibold text-gray-900 dark:text-gray-100 mb-3">Order Items</h3>
                <div className="space-y-3">
                  {order.items.map((item) => (
                    <div key={item.id} className="flex justify-between items-start py-2" data-testid={`order-item-${item.id}`}>
                      <div className="flex-1">
                        <p className="font-medium text-gray-900 dark:text-gray-100 line-clamp-2" data-testid={`text-item-name-${item.id}`}>{item.productName}</p>
                        <p className="text-sm text-gray-600 dark:text-gray-300 mt-1">
                          Qty: {item.quantity} Ã— â‚¹{parseFloat(item.price.replace(/[^\d.]/g, '')).toFixed(2)}
                        </p>
                      </div>
                      <p className="font-semibold text-gray-900 dark:text-gray-100" data-testid={`text-item-total-${item.id}`}>
                        â‚¹{parseFloat(item.totalPrice.replace(/[^\d.]/g, '')).toFixed(2)}
                      </p>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    </div>
    </div>
  );
}

client/src/pages/wishlist.tsx.content:
import { Link } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { ArrowLeft, Heart, ShoppingCart, Trash2 } from "lucide-react";
import { Header } from "@/components/header";
import { CartSidebar } from "@/components/cart-sidebar";
import { AuthModal } from "@/components/auth-modal";
import { useState } from "react";
import { useCart } from "@/hooks/use-cart";
import { useWishlist } from "@/hooks/use-wishlist";
import { useAuth } from "@/hooks/use-auth";
import { useToast } from "@/hooks/use-toast";
import { Skeleton } from "@/components/ui/skeleton";

export default function Wishlist() {
  const [showCart, setShowCart] = useState(false);
  const [showAuth, setShowAuth] = useState(false);
  const { wishlistItems, removeFromWishlist, isLoading } = useWishlist();
  const { addToCart, isLoading: cartLoading } = useCart();
  const { user } = useAuth();
  const { toast } = useToast();

  const handleAddToCart = async (productId: string, productName: string) => {
    if (!user) {
      setShowAuth(true);
      return;
    }

    try {
      await addToCart({ productId, quantity: 1 });
      toast({
        title: "Added to cart",
        description: `${productName} has been added to your cart.`,
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to add product to cart.",
        variant: "destructive",
      });
    }
  };

  const handleRemoveFromWishlist = async (productId: string, productName: string) => {
    try {
      await removeFromWishlist(productId);
      toast({
        title: "Removed from wishlist",
        description: `${productName} has been removed from your wishlist.`,
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to remove from wishlist.",
        variant: "destructive",
      });
    }
  };

  return (
    <div className="min-h-screen bg-background">
      <Header 
        onSearch={() => {}}
        onCartToggle={() => setShowCart(true)} 
        onAuthToggle={() => setShowAuth(true)}
      />

      <div className="container mx-auto px-4 py-8">
        {/* Back button */}
        <Link href="/shop">
          <Button variant="ghost" className="mb-6">
            <ArrowLeft className="h-4 w-4 mr-2" />
            Back to shopping
          </Button>
        </Link>

        {/* Page title */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold mb-2">My Wishlist</h1>
          <p className="text-muted-foreground">
            {wishlistItems?.length || 0} {wishlistItems?.length === 1 ? 'item' : 'items'} saved
          </p>
        </div>

        {/* Loading state */}
        {isLoading ? (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {[...Array(6)].map((_, i) => (
              <Card key={i}>
                <CardContent className="p-6">
                  <Skeleton className="h-48 w-full mb-4" />
                  <Skeleton className="h-4 w-3/4 mb-2" />
                  <Skeleton className="h-4 w-1/2 mb-4" />
                  <Skeleton className="h-10 w-full" />
                </CardContent>
              </Card>
            ))}
          </div>
        ) : wishlistItems && wishlistItems.length > 0 ? (
          // Wishlist items
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {wishlistItems.map((item: any) => (
              <Card key={item.id} className="overflow-hidden">
                <Link href={`/product/${item.product.id}`}>
                  <div className="relative">
                    <img
                      src={item.product.imgLink || "https://images.unsplash.com/photo-1583863788434-e58a36330cf0?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=300"}
                      alt={item.product.productName}
                      className="w-full h-48 object-cover"
                      data-testid={`img-wishlist-${item.product.id}`}
                    />
                    {item.product.discountPercentage && (
                      <div className="absolute top-2 left-2 bg-destructive text-destructive-foreground text-xs px-2 py-1 rounded">
                        {item.product.discountPercentage} OFF
                      </div>
                    )}
                  </div>
                </Link>

                <CardContent className="p-4">
                  <Link href={`/product/${item.product.id}`}>
                    <h3 className="font-medium text-sm mb-2 line-clamp-2 hover:text-primary transition-colors" data-testid={`text-name-${item.product.id}`}>
                      {item.product.productName}
                    </h3>
                  </Link>

                  <div className="flex items-center space-x-2 mb-4">
                    <span className="text-lg font-bold text-primary" data-testid={`text-price-${item.product.id}`}>
                      {item.product.discountedPrice}
                    </span>
                    <span className="text-sm text-muted-foreground line-through">
                      {item.product.actualPrice}
                    </span>
                  </div>

                  <div className="flex space-x-2">
                    <Button
                      size="sm"
                      className="flex-1"
                      onClick={() => handleAddToCart(item.product.id, item.product.productName)}
                      disabled={cartLoading}
                      data-testid={`button-add-cart-${item.product.id}`}
                    >
                      <ShoppingCart className="h-4 w-4 mr-1" />
                      Add to Cart
                    </Button>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => handleRemoveFromWishlist(item.product.id, item.product.productName)}
                      data-testid={`button-remove-wishlist-${item.product.id}`}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        ) : (
          // Empty state
          <Card className="max-w-md mx-auto">
            <CardContent className="p-12 text-center">
              <Heart className="h-16 w-16 mx-auto mb-4 text-muted-foreground" />
              <h2 className="text-xl font-semibold mb-2">Your wishlist is empty</h2>
              <p className="text-muted-foreground mb-6">
                Start adding products you love to your wishlist!
              </p>
              <Link href="/shop">
                <Button>
                  <ShoppingCart className="h-4 w-4 mr-2" />
                  Start Shopping
                </Button>
              </Link>
            </CardContent>
          </Card>
        )}
      </div>

      <CartSidebar 
        isOpen={showCart} 
        onClose={() => setShowCart(false)} 
      />

      <AuthModal 
        isOpen={showAuth} 
        onClose={() => setShowAuth(false)} 
      />
    </div>
  );
}

client/src/hooks/use-semantic-search.ts.content:
import { useMutation } from "@tanstack/react-query";
import { Product } from "@shared/schema";

interface SemanticSearchRequest {
  query: string;
  n_results?: number;
}

interface SemanticSearchResponse {
  products: Product[];
}

const SEMANTIC_SEARCH_API = "https://product-search.replit.app/search";
const API_TOKEN = "*ULXrUUDkkjRheg3cjpQAcBbzGgffZBn!32ssr8JRW9VERcVmweQqGnYi!Y8jcPnG";

export function useSemanticSearch() {
  return useMutation<SemanticSearchResponse, Error, SemanticSearchRequest>({
    mutationFn: async ({ query, n_results = 20 }) => {
      const response = await fetch(SEMANTIC_SEARCH_API, {
        method: "POST",
        headers: {
          "accept": "application/json",
          "Authorization": `Bearer ${API_TOKEN}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          query,
          n_results,
        }),
      });

      if (!response.ok) {
        throw new Error("Failed to perform semantic search");
      }

      const data = await response.json();

      // Map the response to match our Product interface if needed
      // Assuming the API returns products in a compatible format
      return {
        products: data.results || data.products || data,
      };
    },
  });
}

client/src/pages/settings.tsx.content:
import { useState, useEffect } from "react";
import { useAuth } from "@/hooks/use-auth";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogFooter } from "@/components/ui/dialog";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { toast } from "@/hooks/use-toast";
import { Key, Copy, Trash2, Plus, Calendar, Clock, Settings as SettingsIcon } from "lucide-react";
import { format } from "date-fns";
import { queryClient } from "@/lib/queryClient";

interface ApiToken {
  id: string;
  name: string;
  createdAt: string;
  expiresAt: string | null;
  lastUsedAt: string | null;
}

export default function Settings() {
  const { user } = useAuth();
  const [tokens, setTokens] = useState<ApiToken[]>([]);
  const [loading, setLoading] = useState(true);
  const [showCreateDialog, setShowCreateDialog] = useState(false);
  const [showTokenDialog, setShowTokenDialog] = useState(false);
  const [newTokenName, setNewTokenName] = useState("");
  const [newToken, setNewToken] = useState("");
  const [creating, setCreating] = useState(false);
  const [deleting, setDeleting] = useState<string | null>(null);

  useEffect(() => {
    fetchTokens();
  }, []);

  const fetchTokens = async () => {
    try {
      const token = localStorage.getItem('auth_token');
      if (!token) {
        toast({
          title: "Session Expired",
          description: "Please log in again to manage API tokens",
          variant: "destructive"
        });
        setLoading(false);
        return;
      }

      const response = await fetch('/api/tokens', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.status === 401) {
        toast({
          title: "Session Expired",
          description: "Please log in again to manage API tokens",
          variant: "destructive"
        });
        // Redirect to login
        window.location.href = '/login';
        return;
      }

      if (response.ok) {
        const data = await response.json();
        setTokens(data);
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to fetch API tokens",
        variant: "destructive"
      });
    } finally {
      setLoading(false);
    }
  };

  const handleCreateToken = async () => {
    if (!newTokenName.trim()) {
      toast({
        title: "Error",
        description: "Please enter a token name",
        variant: "destructive"
      });
      return;
    }

    setCreating(true);
    try {
      const token = localStorage.getItem('auth_token');
      if (!token) {
        toast({
          title: "Session Expired",
          description: "Please log in again to create API tokens",
          variant: "destructive"
        });
        setShowCreateDialog(false);
        window.location.href = '/login';
        return;
      }

      const response = await fetch('/api/tokens', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ name: newTokenName })
      });

      if (response.status === 401) {
        toast({
          title: "Session Expired",
          description: "Please log in again to create API tokens",
          variant: "destructive"
        });
        setShowCreateDialog(false);
        window.location.href = '/login';
        return;
      }

      if (response.ok) {
        const data = await response.json();
        setNewToken(data.token);
        setShowCreateDialog(false);
        setShowTokenDialog(true);
        setNewTokenName("");
        await fetchTokens();
      } else {
        const error = await response.json();
        toast({
          title: "Error",
          description: error.error || "Failed to create token",
          variant: "destructive"
        });
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to create API token",
        variant: "destructive"
      });
    } finally {
      setCreating(false);
    }
  };

  const handleDeleteToken = async (tokenId: string) => {
    setDeleting(tokenId);
    try {
      const token = localStorage.getItem('auth_token');
      const response = await fetch(`/api/tokens/${tokenId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        toast({
          title: "Success",
          description: "API token deleted successfully"
        });
        await fetchTokens();
      } else {
        toast({
          title: "Error",
          description: "Failed to delete token",
          variant: "destructive"
        });
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to delete API token",
        variant: "destructive"
      });
    } finally {
      setDeleting(null);
    }
  };

  const copyToken = () => {
    navigator.clipboard.writeText(newToken);
    toast({
      title: "Copied!",
      description: "Token copied to clipboard"
    });
  };

  return (
    <div className="container mx-auto py-8 px-4">
      <div className="max-w-4xl mx-auto">
        <div className="flex items-center gap-3 mb-8">
          <SettingsIcon className="h-8 w-8 text-primary" />
          <h1 className="text-3xl font-bold">Settings</h1>
        </div>

        <Card>
          <CardHeader>
            <div className="flex justify-between items-center">
              <div>
                <CardTitle>API Tokens</CardTitle>
                <CardDescription>
                  Manage your API tokens for programmatic access to the API
                </CardDescription>
              </div>
              <Button 
                onClick={() => setShowCreateDialog(true)}
                className="flex items-center gap-2"
                data-testid="button-create-token"
              >
                <Plus className="h-4 w-4" />
                Generate New Token
              </Button>
            </div>
          </CardHeader>
          <CardContent>
            {loading ? (
              <div className="text-center py-8 text-muted-foreground">
                Loading tokens...
              </div>
            ) : tokens.length === 0 ? (
              <div className="text-center py-8">
                <Key className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                <p className="text-muted-foreground">No API tokens yet</p>
                <p className="text-sm text-muted-foreground mt-2">
                  Create your first API token to start using the API
                </p>
              </div>
            ) : (
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Name</TableHead>
                    <TableHead>Created</TableHead>
                    <TableHead>Last Used</TableHead>
                    <TableHead>Expires</TableHead>
                    <TableHead className="text-right">Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {tokens.map((token) => (
                    <TableRow key={token.id}>
                      <TableCell className="font-medium">
                        <div className="flex items-center gap-2">
                          <Key className="h-4 w-4 text-muted-foreground" />
                          {token.name}
                        </div>
                      </TableCell>
                      <TableCell>
                        <div className="flex items-center gap-1 text-sm text-muted-foreground">
                          <Calendar className="h-3 w-3" />
                          {format(new Date(token.createdAt), 'MMM dd, yyyy')}
                        </div>
                      </TableCell>
                      <TableCell>
                        {token.lastUsedAt ? (
                          <div className="flex items-center gap-1 text-sm text-muted-foreground">
                            <Clock className="h-3 w-3" />
                            {format(new Date(token.lastUsedAt), 'MMM dd, yyyy')}
                          </div>
                        ) : (
                          <Badge variant="secondary">Never</Badge>
                        )}
                      </TableCell>
                      <TableCell>
                        {token.expiresAt ? (
                          <Badge variant={new Date(token.expiresAt) < new Date() ? "destructive" : "default"}>
                            {format(new Date(token.expiresAt), 'MMM dd, yyyy')}
                          </Badge>
                        ) : (
                          <Badge variant="outline">No expiry</Badge>
                        )}
                      </TableCell>
                      <TableCell className="text-right">
                        <Button
                          variant="destructive"
                          size="sm"
                          onClick={() => handleDeleteToken(token.id)}
                          disabled={deleting === token.id}
                          data-testid={`button-delete-token-${token.id}`}
                        >
                          <Trash2 className="h-4 w-4" />
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            )}
          </CardContent>
        </Card>

        {/* Create Token Dialog */}
        <Dialog open={showCreateDialog} onOpenChange={setShowCreateDialog}>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Generate New API Token</DialogTitle>
              <DialogDescription>
                Enter a name for your new API token. This will help you identify it later.
              </DialogDescription>
            </DialogHeader>
            <div className="py-4">
              <Input
                placeholder="Token name (e.g., Production API)"
                value={newTokenName}
                onChange={(e) => setNewTokenName(e.target.value)}
                onKeyDown={(e) => e.key === 'Enter' && handleCreateToken()}
                data-testid="input-token-name"
              />
            </div>
            <DialogFooter>
              <Button variant="outline" onClick={() => setShowCreateDialog(false)}>
                Cancel
              </Button>
              <Button onClick={handleCreateToken} disabled={creating}>
                {creating ? "Creating..." : "Create Token"}
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>

        {/* Show Token Dialog */}
        <Dialog open={showTokenDialog} onOpenChange={setShowTokenDialog}>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Your New API Token</DialogTitle>
              <DialogDescription>
                Save this token securely. You won't be able to see it again!
              </DialogDescription>
            </DialogHeader>
            <div className="py-4">
              <div className="bg-muted p-4 rounded-lg font-mono text-sm break-all">
                {newToken}
              </div>
              <Button
                onClick={copyToken}
                className="w-full mt-4"
                variant="outline"
                data-testid="button-copy-token"
              >
                <Copy className="h-4 w-4 mr-2" />
                Copy Token
              </Button>
            </div>
            <DialogFooter>
              <Button onClick={() => {
                setShowTokenDialog(false);
                setNewToken("");
              }}>
                Done
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>
      </div>
    </div>
  );
}

