          }
        },
        Success: {
          type: 'object',
          properties: {
            success: { type: 'boolean' },
            message: { type: 'string' }
          }
        }
      }
    },
    tags: [
      {
        name: 'Authentication',
        description: 'User authentication endpoints'
      },
      {
        name: 'Products',
        description: 'Product catalog operations'
      },
      {
        name: 'Cart',
        description: 'Shopping cart management'
      },
      {
        name: 'Wishlist',
        description: 'Wishlist management'
      },
      {
        name: 'Orders',
